import { useState,useEffect } from "react";
import ChapterReader from './chapterReader.js';
import QuizTaker from './quizTaker.js';
import { useNavigate } from "react-router-dom"; // Import useNavigate
import axios from 'axios';
import './style.css';

function AdhdLearningTool() {
    
    const [chapterStatus, setChapterStatus] = useState({});
    const [output, setOutput] = useState([]);

    const [activeQuiz, setActiveQuiz] = useState(null);
    const [quizComplete,setQuizComplete]=useState(false);
    const [activeChapter, setActiveChapter] = useState(null);
    const [chapterComplete,setChapterComplete]=useState(false);
    const [chapterFlag,setChapterFlag]=useState(true);
    const [quizFlag,setQuizFlag]=useState(true);
    const [chapterText,setChapterText]=useState(null);
    
    const navigate = useNavigate();
    
    useEffect(()=>{

        axios.post('http://192.168.29.110:5001/generate-story')
            .then(res=>{
                console.log("res: "+JSON.stringify(res,null,2));
                if(res.data)
                {
                    const newOutput = res.data.story.map(chapter => ({
                        title: chapter.title,
                        text: chapter.text
                        
                    }));
                    setOutput(newOutput);

                    const newChapterStatus = res.data.data.reduce((acc, chapter) => {
                        acc[chapter.title] = {
                            chapterCompleted: false,
                            quizCompleted: false,
                            score: 0,
                            question:chapter.questions
                        };
                        return acc;
                    }, {});

                    setChapterStatus(newChapterStatus);

                }
            })
            .catch(err=>console.log(err));



    },[])

    function handleAnotherSubmission()
    {
        navigate('//adhdInput');
    }


    function handleChapterClick(index)
    {
        //check pervious chapter status is complete and not be first chapter
        //if not complete show message of complete first
        //else move to pdfviewer

        for(let i=0; i<index; i++)
        {
            if(!chapterStatus[output[i].title].chapterCompleted)
            {
                //chapter is not complete study
                setChapterFlag(false);
                alert(`To open this chapter first,complete pervious chapter along with their quiz.`);
                return;
            }
            else
            {
                if(!chapterStatus[output[i].title].quizCompleted)
                {
                    //show message of not complete quiz
                    alert(`To open this chapter first,complete pervious quiz of chapter number${index+1}.`)
                    setChapterFlag(false);
                    return;
                }
            }
            
        }

        if(chapterFlag)
        {
            //redirect to pdfviewrfunction take two parameter first path and second if complete scroll then update chapter status
            setActiveChapter(output[index].title);
            setChapterText(output[index].text);
        }
        else
            setChapterFlag(true);

    }

    function handleQuizComplete(index)
    {
        //complete all chapters before it along with their quiz
        if(index==0)
        {
            if(!chapterStatus[output[index].title].chapterCompleted)
            {
                //show alert message of not completing its chapter
                setQuizFlag(false);
                alert(`To open this quiz first study the chapter complete`);
                return;
            }
            else
            {
                //redirect to quiz pass score and update status of quiz
                setActiveQuiz(output[index].title);
                //<QuizTaker  updateStatus={setChapterStatus}   chaptertitle={output[index].title}  />
            }
        }
        else
        {
            for(let i=0; i<index; i++)
            {
                if(!chapterStatus[output[i].title].chapterCompleted)
                {
                    //chapter is not complete study
                    setQuizFlag(false);
                    alert(`To open this quiz first study the chapter complete`);
                    return;
                }
                else
                {
                    if(!chapterStatus[output[i].title].quizCompleted)
                    {
                        //show message of not complete quiz
                        setQuizFlag(false);
                        alert(`To open this quiz first complete pervious quiz`);
                        return;
                    }
                }
                    
            }
            if(quizFlag)
            {
                //redirect to quiz reader section 
                setActiveQuiz(output[index].title);
                //<QuizTaker  updateStatus={setChapterStatus}   chapterName={output[index].name}  />
            }
            else
                setQuizFlag(true);
        }
        
    }

    const showList = () => {
    return (
        <div    className="adhdLearningMainTag">
        <div style={{ margin: "20px" }} className="adhdLearning">
            <h4>SubChapters for you:</h4>
        <table style={{ width: "100%", borderCollapse: "collapse", textAlign: "center" }}>
            <thead>
                <tr style={{ backgroundColor: "#ddd", height: "40px" }}>
                    <th style={{ border: "1px solid black", padding: "10px" }}>No</th>
                    <th style={{ border: "1px solid black", padding: "10px" }}>Name</th>
                    <th style={{ border: "1px solid black", padding: "10px" }}>Chapter Read</th>
                    <th style={{ border: "1px solid black", padding: "10px" }}>Quiz</th>
                    <th style={{ border: "1px solid black", padding: "10px" }}>Score</th>
                    
                </tr>
            </thead>
            <tbody>
                {output.map((item, index) => (
                    <tr key={index} style={{ height: "40px" }}>
                        <td style={{ border: "1px solid black", padding: "10px" }}>{index + 1}</td>
                        <td 
                            style={{ border: "1px solid black", padding: "10px", cursor: "pointer", color: "blue" }}
                            onClick={() => handleChapterClick(index)}
                        >
                            {item.title}
                        </td>
                        <td style={{ border: "1px solid black", padding: "10px" }}>
                            {chapterStatus[item.title]?.chapterCompleted ? "✅ Completed" : "⏳ In Progress"}
                        </td>
                        <td style={{ border: "1px solid black", padding: "10px" }}>
                            <button 
                                onClick={() => handleQuizComplete(index)}
                                style={{
                                    cursor: chapterStatus[item.title]?.chapterCompleted ? "pointer" : "not-allowed",
                                    opacity: chapterStatus[item.title]?.chapterCompleted ? 1 : 0.5
                                }}
                            >
                                {chapterStatus[item.title]?.chapterCompleted ? "Take Quiz" : "🔒 Locked"}
                            </button>
                        </td>
                        <td style={{ border: "1px solid black", padding: "10px" }}>
                            {chapterStatus[item.title]?.quizCompleted ? `${chapterStatus[item.title]?.score}` : "❌ Not Attempted"}
                        </td>
                        
                    </tr>
                ))}
            </tbody>
        </table>
            
    </div>
    <button   onClick={handleAnotherSubmission}  id="adhdlearningButton">Upload Another Chapter</button>
    </div>
    );
  };

  return (
    <div>
        {!activeChapter && !activeQuiz && showList()}

        {activeChapter && !activeQuiz && (
            <ChapterReader  
                isChapterComplete={setChapterComplete}
                chapterName={activeChapter}
                updateStatus={setChapterStatus}
                chapterStatus={setActiveChapter}
                chapterText={chapterText}
            />
        )}

        {activeQuiz && !activeChapter && !quizComplete && (
            <QuizTaker
                isQuizComplete={setQuizComplete}
                chapterName={activeQuiz}
                updateStatus={setChapterStatus}
                quizStatus={setActiveQuiz}
                quizQuestion={chapterStatus[activeQuiz].question}
            />
        )}

        
    </div>
  );
}

export default AdhdLearningTool;
