import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import './style.css';

function UserInput() {
    const [file, setFile] = useState(null);
    const [state, setState] = useState("upload");
    const [message, setMessage] = useState("");
    const [flag, setFlag] = useState(false);
    const navigate = useNavigate(); // Initialize navigation

    const handleInputFile = async (e) => {
        e.preventDefault();
        setState("upload");
        setFlag(true);

        if (!file) {
            setState("error");
            return;
        }

        const formData = new FormData();
        formData.append("pdfFile", file);

        try {
            const response = await axios.post("http://192.168.29.110:5001/upload", formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });

            
            setState("success");
            navigate("/adhd-learning"); // Redirect to ADHD Learning Module

        } catch (error) {
            setState("error");
            console.error(error);
        }
    };

    useEffect(() => {
        if (state === "upload") setMessage("Your file is uploading...");
        else if (state === "error") setMessage("Error while parsing the file");
        else if (state === "success") setMessage("File uploaded successfully!");
    }, [state]);

    return (
        <>
        <div   className="adhdInput">
            {!flag && (

                <form onSubmit={handleInputFile}    className="adhdInputForm">
                    <label>Choose your file</label>
                    <input type="file" accept=".pdf" onChange={(e) => setFile(e.target.files[0])} required />
                    <button type="submit">Upload</button>
                </form>
            )}
            {flag && <h6>{message}</h6>}
        </div>
        </>
    );
}

export default UserInput;
