import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import './style.css';

function UserInput() {
      const [pdfFile, setPdfFile] = useState(null);
      const [uploadedFileName, setUploadedFileName] = useState(null);
      const [output, setOutput] = useState("");
      const [isDragging, setIsDragging] = useState(false);
      
        const [file, setFile] = useState(null);
        const [summary, setSummary] = useState("");
        const [loading, setLoading] = useState(false);
        const [error, setError] = useState("");

    const handleInputFile = async (e) => {
        e.preventDefault();
        setState("upload");
        setFlag(true);

        if (!file) {
            setState("error");
            return;
        }

        const formData = new FormData();
        formData.append("pdfFile", file);

        try {
            const response = await axios.post("http://192.168.29.110:5001/upload", formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });

            
            setState("success");
            navigate("/adhd-learning"); // Redirect to ADHD Learning Module

        } catch (error) {
            setState("error");
            console.error(error);
        }
    };

    useEffect(() => {
        if (state === "upload") setMessage("Your file is uploading...");
        else if (state === "error") setMessage("Error while parsing the file");
        else if (state === "success") setMessage("File uploaded successfully!");
    }, [state]);
    const handleFileChange = async (e) => {
        const file = e.target.files[0];
        if (file && file.type === "application/pdf") {
          setPdfFile(file);
          setUploadedFileName(null); // Reset previous upload
          await uploadFile(file);
        } else {
          alert("Please upload a valid PDF file.");
        }
      };

    return (
        <>
        <div className="upload-section">
                <label className="label">Upload PDF:</label>
                <div
                  className={`drop-area ${isDragging ? "drag-active" : ""} flex justify-center flex-col items-center`}
                  onDrop={handleDrop}
                  onDragOver={(e) => { e.preventDefault(); setIsDragging(true); }}
                  onDragLeave={() => setIsDragging(false)}
                  onClick={() => document.getElementById("fileInput").click()}
                >
                  <input
                    id="fileInput"
                    type="file"
                    accept="application/pdf"
                    onChange={handleFileChange}
                    className="hidden-input"
                  />
                  <FaCloudUploadAlt size={50} className="upload-icon" />
                  <p className="drop-text">
                    Drag & Drop your PDF here or <span className="highlight">click to select</span>
                  </p>
                </div>
                {pdfFile && <p className="selected-file">Selected File: {pdfFile.name}</p>}
              </div>
        </>
    );
}

export default UserInput;
