import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { FaCloudUploadAlt } from "react-icons/fa"; // Import missing icon
import './style.css';

function UserInput() {
    const [pdfFile, setPdfFile] = useState(null);
    const [uploadedFileName, setUploadedFileName] = useState(null);
    const [isDragging, setIsDragging] = useState(false);
    const [state, setState] = useState(""); // Added missing state
    const [message, setMessage] = useState(""); // Added missing message state
    const navigate = useNavigate(); // Initialize navigate function

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file && file.type === "application/pdf") {
            setPdfFile(file);
            setUploadedFileName(null); // Reset previous upload
        } else {
            alert("Please upload a valid PDF file.");
        }
    };

    const handleDrop = (e) => {
        e.preventDefault();
        setIsDragging(false);
        const file = e.dataTransfer.files[0];
        if (file && file.type === "application/pdf") {
            setPdfFile(file);
            setUploadedFileName(null);
        } else {
            alert("Please upload a valid PDF file.");
        }
    };

    const handleInputFile = async (e) => {
        e.preventDefault();
        setState("upload");

        if (!pdfFile) {
            setState("error");
            return;
        }

        const formData = new FormData();
        formData.append("pdfFile", pdfFile);

        try {
            await axios.post("http://192.168.29.110:5001/upload", formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });

            setState("success");
            navigate("/adhd-learning"); // Redirect to ADHD Learning Module
        } catch (error) {
            setState("error");
            console.error(error);
        }
    };

    useEffect(() => {
        if (state === "upload") setMessage("Your file is uploading...");
        else if (state === "error") setMessage("Error while parsing the file");
        else if (state === "success") setMessage("File uploaded successfully!");
    }, [state]);

    return (
        <>
            <div className="upload-section">
                <label className="label">Upload PDF:</label>
                <div
                    className={`drop-area ${isDragging ? "drag-active" : ""} flex justify-center flex-col items-center`}
                    onDrop={handleDrop}
                    onDragOver={(e) => { e.preventDefault(); setIsDragging(true); }}
                    onDragLeave={() => setIsDragging(false)}
                    onClick={() => document.getElementById("fileInput").click()}
                >
                    <input
                        id="fileInput"
                        type="file"
                        accept="application/pdf"
                        onChange={handleFileChange}
                        className="hidden-input"
                    />
                    <FaCloudUploadAlt size={50} className="upload-icon" />
                    <p className="drop-text">
                        Drag & Drop your PDF here or <span className="highlight">click to select</span>
                    </p>
                </div>
                {pdfFile && <p className="selected-file">Selected File: {pdfFile.name}</p>}
                <button onClick={handleInputFile} className="upload-btn">Upload</button>
                {message && <p className="message">{message}</p>}
            </div>
        </>
    );
}

export default UserInput;
